INSERT INTO homestead.products (id, name, price, code, imagePath, manufacturer, platform, language, game_type, pegi_rating, description, multiplayer, created_at, updated_at) VALUES (3, 'Kingdom Come Deliverance', 60, 'JXDD23', 'kingdom_come_deliverance.jpg', 'Warhorse Studios', 'PC, PlayStation 4, XBOX ONE', 'inglise', 'Action RPG', '18', 'You''re Henry, the son of a blacksmith. Thrust into a raging civil war, you watch helplessly as invaders storm your village and slaughter your friends and family. Narrowly escaping the brutal attack, you grab your sword to fight back. Avenge the death of your parents and help repel the invading forces!', 0, '2020-06-16 15:42:52', '2020-06-16 15:55:00');
INSERT INTO homestead.products (id, name, price, code, imagePath, manufacturer, platform, language, game_type, pegi_rating, description, multiplayer, created_at, updated_at) VALUES (4, 'Minecraft', 20, 'JKGG21', 'minecraft.jpg', 'Mojang', 'PC, PlayStation 3, PlayStation 4, XBOX 360, XBOX ONE', 'eesti', 'sandbox', '6', 'The original version of Minecraft! Java Edition has cross-platform play between Windows, Linux and macOS, and also supports user-created skins and mods. Includes a decade’s worth of updates, with much more to come!', 1, '2020-06-16 15:56:40', '2020-06-16 15:56:40');
INSERT INTO homestead.products (id, name, price, code, imagePath, manufacturer, platform, language, game_type, pegi_rating, description, multiplayer, created_at, updated_at) VALUES (5, 'The Witcher 3: Wild Hunt', 5, 'DSSA22', 'witcher3.jpg', 'CD Projekt Red', 'PC, PlayStation 4, XBOX ONE', 'eesti', 'Action RPG', '16', 'The Witcher: Wild Hunt is a story-driven open world RPG set in a visually stunning fantasy universe full of meaningful choices and impactful consequences. In The Witcher, you play as professional monster hunter Geralt of Rivia tasked with finding a child of prophecy in a vast open world rich with merchant cities, pirate islands, dangerous mountain passes, and forgotten caverns to explore.', 0, '2020-06-16 16:01:18', '2020-06-16 16:01:18');
INSERT INTO homestead.products (id, name, price, code, imagePath, manufacturer, platform, language, game_type, pegi_rating, description, multiplayer, created_at, updated_at) VALUES (6, 'Cyberpunk 2077', 60, 'HGD22', 'cyberpunk.jpg', 'CD Projekt Red', 'PC, PlayStation 4, XBOX ONE', 'eesti', 'RPG', '18', 'Cyberpunk 2077 is an open-world, action-adventure story set in Night City, a megalopolis obsessed with power, glamour and body modification. You play as V, a mercenary outlaw going after a one-of-a-kind implant that is the key to immortality. You can customize your character’s cyberware, skillset and playstyle, and explore a vast city where the choices you make shape the story and the world around you.', 1, '2020-06-16 16:04:35', '2020-06-16 16:04:41');
INSERT INTO homestead.products (id, name, price, code, imagePath, manufacturer, platform, language, game_type, pegi_rating, description, multiplayer, created_at, updated_at) VALUES (7, 'DIRT Rally', 20, 'JGX22', 'DIRT.jpg', 'CodeMasters', 'PC, PlayStation 4', 'inglise', 'Racing Simulation', '3', 'DiRT Rally is the most authentic and thrilling rally game ever made, road-tested over 80 million miles by the DiRT community. It perfectly captures that white knuckle feeling of racing on the edge as you hurtle along dangerous roads at breakneck speed, knowing that one crash could irreparably harm your stage time.
INSERT INTO homestead.products (id, name, price, code, imagePath, manufacturer, platform, language, game_type, pegi_rating, description, multiplayer, created_at, updated_at) VALUES (8, 'Thief', 20, 'GGJH2', 'thief.jpg', 'Eidos-Montréal', 'PC', 'inglise', 'Action adventure', '18', 'Garrett, the Master Thief, steps out of the shadows into the City. In this treacherous place, where the Baron’s Watch spreads a rising tide of fear and oppression, his skills are the only things he can trust. Even the most cautious citizens and their best-guarded possessions are not safe from his reach.
INSERT INTO homestead.products (id, name, price, code, imagePath, manufacturer, platform, language, game_type, pegi_rating, description, multiplayer, created_at, updated_at) VALUES (9, 'The Elder Scrolls V: Skyrim Special Edition', 60, 'KJD66', 'skyrim.jpg', 'Bethesda Game Studios', 'PC, PlayStation 4, XBOX ONE', 'eesti', 'RPG', '18', 'Winner of more than 200 Game of the Year Awards, Skyrim Special Edition brings the epic fantasy to life in stunning detail. The Special Edition includes the critically acclaimed game and add-ons with all-new features like remastered art and effects, volumetric god rays, dynamic depth of field, screen-space reflections, and more. Skyrim Special Edition also brings the full power of mods to the PC and consoles. New quests, environments, characters, dialogue, armor, weapons and more – with Mods, there are no limits to what you can experience.', 0, '2020-06-16 16:13:57', '2020-06-16 16:13:57');
INSERT INTO homestead.products (id, name, price, code, imagePath, manufacturer, platform, language, game_type, pegi_rating, description, multiplayer, created_at, updated_at) VALUES (10, 'DOOM Eternal', 60, 'HHHS2', 'doom_ETERNAL.jpg', 'id Software', 'PC, PS4, Stadia, Xbox One', 'inglise', 'FPS', '18', 'Hell’s armies have invaded Earth. Become the Slayer in an epic single-player campaign to conquer demons across dimensions and stop the final destruction of humanity.', 1, '2020-06-16 16:18:11', '2020-06-16 16:18:11');
INSERT INTO homestead.products (id, name, price, code, imagePath, manufacturer, platform, language, game_type, pegi_rating, description, multiplayer, created_at, updated_at) VALUES (11, 'DOOM', 60, 'AAS23', 'doom.jpg', 'id Software', 'PC', 'inglise', 'FPS', '18', 'Developed by id software, the studio that pioneered the first-person shooter genre and created multiplayer Deathmatch, DOOM returns as a brutally fun and challenging modern-day shooter experience. Relentless demons, impossibly destructive guns, and fast, fluid movement provide the foundation for intense, first-person combat – whether you’re obliterating demon hordes through the depths of Hell in the single-player campaign, or competing against your friends in numerous multiplayer modes. Expand your gameplay experience using DOOM SnapMap game editor to easily create, play, and share your content with the world.', 1, '2020-06-16 16:19:25', '2020-06-16 16:19:25');